sys_script.script:
//new x_snc_dh_pers_int.PersonaInteractions().hasInteractions(current,current.comments ? current.comments.getJournalEntry(1) : current.work_notes.getJournalEntry(1));
(function executeRule(current, previous /*null when async*/) {

	var subflow_sys_ids = new x_snc_dh_pers_int.PersonaInteractions().getInteractions(current,current.comments ? current.comments.getJournalEntry(1) : current.work_notes.getJournalEntry(1));

	var input_text = current.comments ? current.comments.getJournalEntry(1) : current.work_notes.getJournalEntry(1);
	var contains_mentions = input_text.match(/\@\[(.*?)\]/gm);

	if(contains_mentions){

		try {
			var inputs = {};
			inputs['record'] = current.sys_id; 
			inputs['table'] = current.sys_class_name;
			inputs['subflows'] = subflow_sys_ids.join();
			var sys_user = "";
			if(current.comments){
				inputs['type'] = "comments";
				inputs['message'] = current.comments + "";
				var comment = current.comments.getJournalEntry(1);
				var temp_match_c = comment.match(/\[(.*?)\]/);
				var sys_user_c = temp_match_c[1].split(":");
				sys_user = sys_user_c[0];
			}else if(current.work_notes){
				inputs['message'] = current.work_notes + "";
				inputs['type'] = "work_notes";
				var work_notes = current.work_notes.getJournalEntry(1);
				var temp_match_w_n = work_notes.match(/\[(.*?)\]/);
				var sys_user_w_n = temp_match_w_n[1].split(":");
				sys_user = sys_user_w_n[0];
			}
			inputs['mention'] = sys_user;
			sn_fd.FlowAPI.getRunner().subflow('global.persona_interaction_main').inBackground().withInputs(inputs).run();	
		} catch (ex) {
			var message = ex.getMessage();
			gs.error(message);
		}
	}

})(current, previous);
